import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { FormControl, ReactiveFormsModule } from '@angular/forms';
import { DomSanitizer, SafeResourceUrl } from '@angular/platform-browser';
import { FormBuilder } from '@angular/forms';import { RouterOutlet } from '@angular/router';
import { Supabase } from '../../utils/images';
import { PropertyService } from '../../services/supabase/property.service';
interface Casa {
  id:string;
  rooms: string;
  title: string;
  description: string;
  price: string;
  country: string;
  city: string;
  owner: string;
  area: string;
  negotiable: string;
  type: string;
  image: string;
  mapSrc: string;
  date: string;
  searchTerm?: string;
}

@Component({
  selector: 'app-search-bar',
  standalone: true,
  imports: [RouterOutlet,ReactiveFormsModule],
  templateUrl: './search-bar.component.html',
  styleUrl: './search-bar.component.css'
})
export class SearchBarComponent implements OnInit {
  constructor(private propertyService: PropertyService) {}
  casas:any;
  IsGetCasas:boolean=false;
// listas de acumulacion de paices para evitar filtros con paices o ciudades repetidas
listCountrys = [""];
listCitys = [""];
ngOnInit(): void {
  if(this.IsGetCasas==false) {
    
    this.updatebuyerhouse();
    this.IsGetCasas=true;
  }
  // Verificar el rol del usuario desde localStorage
}

updatebuyerhouse() {  

  this.propertyService.getAllProperties().subscribe(
    (counter) => {
      console.log(counter);
      this.casas=counter
    },
    (error) => {
      console.error('Error al cargar historial de mensajes:', error);
    }
  );

}


// variables del reactive form
  rooms = new FormControl('');
  searchtermn = new FormControl('');
  country = new FormControl('');
  city = new FormControl('');
  price_per_nig = new FormControl('');
  num_bedrooms = new FormControl('');
  max_guests = new FormControl('');
  num_bathrooms= new FormControl('');



// variables que el componente va a devolver a la pagina que lo contendra para saber que filtros estan vigentes
@Output() setCriterios: EventEmitter<any> = new EventEmitter<string>();
@Output() setSearchtermn: EventEmitter<any> = new EventEmitter<string>();
@Output() setPrice_per_nig: EventEmitter<string> = new EventEmitter<string>();
  @Output() setMax_guests: EventEmitter<string> = new EventEmitter<string>();
  @Output() setNum_bedrooms: EventEmitter<string> = new EventEmitter<string>();
  @Output() setCity: EventEmitter<string> = new EventEmitter<string>();
  @Output() setCountry: EventEmitter<string> = new EventEmitter<string>();
  @Output() setNum_bathrooms: EventEmitter<string> = new EventEmitter<string>();

  // funcion para enviar los filtros selecionados 
  
  enviarValor() {    
        this.setCriterios.emit(
      {
        "max_guests":this.max_guests.value!,  
        "searchtermn":this.searchtermn.value!,  
        "price_per_nig":this.price_per_nig.value!,
        "num_bathrooms":this.num_bathrooms.value!,
        "num_bedrooms":this.num_bedrooms.value!,
        "city":this.city.value!,
        "country":this.country.value!,
      }
      
      );
  }

// funcion para resetear todos los filtros a 0
  setReset(){
    this.price_per_nig = new FormControl('');
    this.country = new FormControl('');
    this.city = new FormControl('');
    this.num_bedrooms = new FormControl('');
    this.num_bathrooms = new FormControl('');
    this.max_guests = new FormControl('');
    this.setCriterios.emit(
      {
        "price_per_nig":this.price_per_nig.value!,  
        "num_bedrooms":this.num_bedrooms.value!,  
        "num_bathrooms":this.num_bathrooms.value!,
        "max_guests":this.max_guests.value!,
        "searchtermn":this.searchtermn.value!,
        "city":this.city.value!,
        "country":this.country.value!,
      }
    );
  
  }
  
}
<form>
  <!-- Searchbar y boton de guardar parametros -->
  <div class="search-container">
    <input
      class="search-input"
      [formControl]="searchtermn"
      name="searchbar"
      required
    />
    <!-- boton para guardar los filtros de busqueda y de consulta por criterios -->
  </div>
  <div class="card-filter" name="contenedor-filtros-center">
    <div class="margins" name="contenedor ubicación">
      <!-- Control de selecion de pais segun el pais selecionado -->

      <select class="margins" [formControl]="country" name="pais" id="cars">
        <option hidden value="">pais</option>
        <option value="">mostrar todo</option>
        <!-- for para compilar los diferentes paises sin repetirse -->
        @for (item of this.casas; track item.title) {
        @if(this.listCountrys.includes(item.country)==false){
        {{
          this.listCountrys.push(item.country)
        }}
        } }
        <!-- for que trae los paises como opiones  -->
        @for (item of listCountrys; track item) { @if(item!="") {
        <option value="{{ item }}">{{ item }}</option>
        } }
      </select>
      <div>
        <!-- Control de selecion de ciudad segun el pais selecionado -->

        <select class="margins" [formControl]="city" name="ciudad" id="city">
          <option hidden value="">ciudad</option>
          <option value="">mostrar todo</option>
          @for (item of casas; track item.title) { @if(this.country.value!==""){
          @if(item.country==this.country.value){
          <option value="{{ item.city }}">{{ item.city }}</option>
          } } }
        </select>

      </div>
    </div>

    <div class="margins" name="contenedor fecha">
        <!-- Control de selecion de numero de habitaciones de 1 a 3 y si es 4 se buscaran mayores a 3 -->

      <label>numero de baños</label>
      <input type="number" name="num_bathrooms" [formControl]="num_bathrooms" required />
      <div>
        <label>numero de habitaciones</label>

        <input type="number" name="num_bedrooms" [formControl]="num_bedrooms" required />
      </div>
    </div>

    <!-- Filtro por intevalo de precio -->

    <div class="margins contenedor precio">
      <label>precio por noche</label>
      <input type="number" name="price_per_nig" [formControl]="price_per_nig" required />
      <div>
        <label>maximo de huespedes</label>
        <input
          class=""
          type="number"
          name="max_guests"
          defaultValue="max"
          [formControl]="max_guests"
          required
        />
      </div>
    </div>

    <div>
      <!-- boton de reseteo de los campos-->
      <button
        type="button"
        (click)="$event.preventDefault(); setReset()"
        class="button"
        type="submit"
      >
        Reset
      </button>
      <button
      class="button"
      (click)="$event.preventDefault(); enviarValor()"
    >
      Aplicar
    </button>

    </div>
  </div>
</form>
